import { storage } from "../storage";
import { uploadImageToImgbb } from "./imgbb";

const HF_API_URL = "https://api-inference.huggingface.co/models/";
const MODELS_TO_TRY = [
  "stabilityai/stable-diffusion-2-1",
  "Lykon/dreamshaper-8",
  "prompthero/openjourney-v4",
  "runwayml/stable-diffusion-v1-5",
];
const REQUEST_TIMEOUT = 60000;

async function getHuggingfaceApiKey(): Promise<string> {
  const key = await storage.getNextHuggingfaceKey();
  if (!key) {
    throw new Error("No HuggingFace API keys available. Please add a key in settings.");
  }
  return key.key;
}

async function fetchWithTimeout(url: string, options: RequestInit, timeout: number): Promise<Response> {
  const controller = new AbortController();
  const timeoutId = setTimeout(() => controller.abort(), timeout);

  try {
    const response = await fetch(url, {
      ...options,
      signal: controller.signal,
    });
    clearTimeout(timeoutId);
    return response;
  } catch (error) {
    clearTimeout(timeoutId);
    throw error;
  }
}

export async function testHuggingfaceConnection(): Promise<{ success: boolean; message: string }> {
  try {
    const apiKey = await getHuggingfaceApiKey();

    for (const model of MODELS_TO_TRY) {
      try {
        const response = await fetchWithTimeout(`${HF_API_URL}${model}`, {
          method: "POST",
          headers: {
            Authorization: `Bearer ${apiKey}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            inputs: "test",
            options: { wait_for_model: false },
          }),
        }, 10000);

        if (response.status === 401) {
          return { success: false, message: "Invalid API key" };
        }

        if (response.status === 503 || response.ok || response.status === 200) {
          return { success: true, message: `HuggingFace API connection verified (${model})` };
        }
      } catch {
        continue;
      }
    }

    return { success: true, message: "Connection established (models may be loading)" };
  } catch (error) {
    const message = error instanceof Error ? error.message : "Unknown error";
    return { success: false, message: `Connection failed: ${message}` };
  }
}

export async function generateImage(prompt: string): Promise<string | null> {
  try {
    const apiKey = await getHuggingfaceApiKey();

    const enhancedPrompt = `${prompt}, high quality, professional photography, sharp focus, vivid colors, detailed, 8k resolution`;
    console.log("[HuggingFace] Generating image with prompt:", enhancedPrompt.substring(0, 100) + "...");

    for (const model of MODELS_TO_TRY) {
      try {
        console.log(`[HuggingFace] Trying model: ${model}`);

        const requestBody: Record<string, unknown> = {
          inputs: enhancedPrompt,
          parameters: {
            num_inference_steps: 30,
            guidance_scale: 7.5,
            width: 1024,
            height: 576,
          },
          options: { 
            wait_for_model: true,
            use_cache: false,
          },
        };

        console.log(`[HuggingFace] Request to ${model}:`, JSON.stringify(requestBody).substring(0, 200));

        const response = await fetchWithTimeout(`${HF_API_URL}${model}`, {
          method: "POST",
          headers: {
            Authorization: `Bearer ${apiKey}`,
            "Content-Type": "application/json",
            "Accept": "image/png, image/jpeg, application/json",
          },
          body: JSON.stringify(requestBody),
        }, REQUEST_TIMEOUT);

        console.log(`[HuggingFace] Response status: ${response.status}, content-type: ${response.headers.get("content-type")}`);

        if (response.ok) {
          const contentType = response.headers.get("content-type") || "";

          if (contentType.includes("image")) {
            const imageUrl = await saveImageFromResponse(response);
            if (imageUrl) {
              console.log(`[HuggingFace] Image generated successfully with model: ${model}`);
              return imageUrl;
            }
          } else {
            const text = await response.text();
            console.log(`[HuggingFace] Model ${model} returned non-image response:`, text.substring(0, 300));
          }
        } else if (response.status === 503) {
          const errorData = await response.json().catch(() => ({}));
          const estimatedTime = (errorData as { estimated_time?: number }).estimated_time;
          console.log(`[HuggingFace] Model ${model} is loading (estimated: ${estimatedTime}s), trying next...`);
        } else if (response.status === 500) {
          const errorText = await response.text().catch(() => "");
          console.log(`[HuggingFace] Model ${model} server error: ${errorText.substring(0, 200)}`);
        } else if (response.status === 401) {
          console.log(`[HuggingFace] Model ${model} auth failed - check API key`);
        } else if (response.status === 403) {
          console.log(`[HuggingFace] Model ${model} access denied - may require Pro subscription`);
        } else {
          const errorText = await response.text().catch(() => "");
          console.log(`[HuggingFace] Model ${model} failed with status ${response.status}: ${errorText.substring(0, 200)}`);
        }
      } catch (err) {
        const errorMsg = err instanceof Error ? err.message : "Unknown error";
        if (errorMsg.includes("aborted") || errorMsg.includes("AbortError")) {
          console.log(`[HuggingFace] Model ${model} timed out after ${REQUEST_TIMEOUT/1000}s`);
        } else {
          console.error(`[HuggingFace] Model ${model} error:`, errorMsg);
        }
      }
    }

    console.error("[HuggingFace] All models failed to generate image");
    return null;
  } catch (error) {
    console.error("[HuggingFace] Error generating image:", error);
    return null;
  }
}

async function saveImageFromResponse(response: Response): Promise<string | null> {
  try {
    const arrayBuffer = await response.arrayBuffer();
    const buffer = Buffer.from(arrayBuffer);

    const contentType = response.headers.get("content-type") || "image/png";
    const mimeType = contentType.includes("jpeg") || contentType.includes("jpg") ? "image/jpeg" : "image/png";

    const base64 = buffer.toString("base64");
    const dataUrl = `data:${mimeType};base64,${base64}`;

    console.log("[HuggingFace] Image converted to base64 data URL (length:", dataUrl.length, ")");

    const hostedUrl = await uploadImageToImgbb(dataUrl);
    if (hostedUrl) {
      console.log("[HuggingFace] Image uploaded to ImgBB, using hosted URL:", hostedUrl);
      return hostedUrl;
    }

    console.log("[HuggingFace] ImgBB upload failed or not configured, falling back to base64 data URL");
    return dataUrl;
  } catch (error) {
    console.error("[HuggingFace] Error saving image:", error);
    return null;
  }
}

function getStockImageUrl(prompt: string): string {
  const randomId = Math.floor(Math.random() * 1000) + 1;
  return `https://picsum.photos/seed/${randomId}/1200/630`;
}

export async function generateBlogImage(prompt: string, maxRetries = 2): Promise<string | null> {
  console.log("[HuggingFace] Starting blog image generation...");
  console.log("[HuggingFace] Prompt:", prompt.substring(0, 100) + "...");

  let retries = 0;

  while (retries < maxRetries) {
    console.log(`[HuggingFace] Attempt ${retries + 1}/${maxRetries}...`);
    try {
      const image = await generateImage(prompt);
      if (image) {
        console.log("[HuggingFace] Blog image generated successfully!");
        return image;
      }
      console.log(`[HuggingFace] Attempt ${retries + 1} returned null, retrying...`);
    } catch (error) {
      console.error(`[HuggingFace] Attempt ${retries + 1} failed:`, error);
    }
    retries++;
    if (retries < maxRetries) {
      console.log("[HuggingFace] Waiting 3 seconds before retry...");
      await new Promise((resolve) => setTimeout(resolve, 3000));
    }
  }

  console.log("[HuggingFace] AI image generation failed, using stock image fallback...");
  const stockImageUrl = getStockImageUrl(prompt);
  console.log("[HuggingFace] Stock image URL:", stockImageUrl);
  return stockImageUrl;
}

