});

export type BloggerAccount = z.infer<typeof bloggerAccountSchema>;

export const insertBloggerAccountSchema = bloggerAccountSchema.omit({ 
  id: true, 
  createdAt: true,
  accessToken: true,
  tokenExpiry: true,
  blogName: true,
  blogUrl: true,
  isConnected: true,
});
